    @kotlinx.serialization.Serializable
    data class TempPhigrosData(
        val name: String,
        val names: List<String>?,
        val path: String,
        val from: String
    )

    @JvmStatic
    fun main(args: Array<String>) {
        val url = "https://zh.moegirl.org.cn/Phigros/%E8%B0%B1%E9%9D%A2%E4%BF%A1%E6%81%AF"
        val temp = (Json.decodeFromString<List<TempPhigrosData>>(temp)).associateBy {
            it.name
        }
        val html = Jsoup.connect(url).get()
        val songs = mutableListOf<PhigrosSong>()
        val tables = html.select("table.wikitable")
        for (table in tables) {
            val rows = table.select("tr")
            val array = arrayListOf<Any>()
            for ((index, row) in rows.withIndex()) {
                kotlin.runCatching {
                    when (index) {
                        0 -> array.add(row.select("th").text()) // name
                        2 -> array.add(row.select("td:eq(1)").text()) //chapter
                        3 -> {
                            array.add(row.select("td:eq(1)").text().replace(" ", "").toBpm()) //bpm
                            array.add(row.select("td:eq(3)").text()) //author
                        }
                        4 -> {
                            array.add(row.select("td:eq(1)").text()) //length
                            array.add(row.select("td:eq(3)").text()) //drawer
                        }
                        in 6..9 -> {
                            val level = PhigrosSongLevel(
                                row.select("td:eq(4)").text(),
                                row.select("td:eq(1)").text().toInt(),
                                row.select("td:eq(2)").text().toDoubleOrNull() ?: 2.0,
                                row.select("td:eq(3)").text().toInt(),
                            )
                            array.add(level)
                        }
                        else -> throw IllegalArgumentException()
                    }
                }.recoverCatching {
                    throw it.apply { it.addSuppressed(IllegalArgumentException(row.text())) }
                }
                if (index == rows.lastIndex) {
                    println(array)
                    val chapter = array[1].toString()

                    val unlock =
                        if (chapter == "Chapter Legacy 过去的章节" || (chapter != "单曲精选集" && songs.lastOrNull()?.chapter != chapter)) {
                            "直接解锁"
                        }else if(chapter == "单曲精选集") {
                            "花费 16.00MB 解锁"
                        }
                        else {
                            "${songs.last().masterName} 任意等级达到 A 评分解锁"
                        }
                    val masterName = array[0].toString()
                    val equalMasterName = masterName.lowercase()
                    val song = PhigrosSong(
                        masterName = masterName,
                        chapter = chapter,
                        bpm = array[2] as Double,
                        songAuthor = array[3].toString(),
                        songLength = array[4].toString(),
                        illustration = array[5].toString(),
                        level = object : HashMap<PhigrosSongLevel.Level, PhigrosSongLevel>() {
                            init {
                                this[PhigrosSongLevel.Level.EZ] = array[6] as PhigrosSongLevel
                                if (array.lastIndex > 6) {
                                    this[PhigrosSongLevel.Level.HD] = array[7] as PhigrosSongLevel
                                    this[PhigrosSongLevel.Level.IN] = array[8] as PhigrosSongLevel
                                    if (array.lastIndex > 8) {
                                        println("AT")
                                        this[PhigrosSongLevel.Level.AT] = array[9] as PhigrosSongLevel
                                    }
                                }
                            }
                        },
                        from = temp[equalMasterName]?.from ?: "UNKNOWN",
                        illustrationPath = temp[equalMasterName]?.path ?: "UNKNOWN",
                        names = temp[equalMasterName]?.names?.drop(1) ?: listOf(),
                        unlock = unlock
                    )
                    songs.add(song)
                }

            }
        }
        println(Json.encodeToJsonElement(songs))
    }

    fun String.toBpm() =
        toDoubleOrNull() ?: split("~")
            .map { it.toDoubleOrNull() ?: 0.0 }
            .average()
}